mapper.insert:
		insert into search_info
	 		(
	 			client_ip
	 			, menu
	 			, search_word
	 			, reg_date
	 		)
 		values
	 		(
	 			#{client_ip}
	 			, #{menu}
	 			, #{search_word}
	 			, now()
	 		)

		insert into click_info
	 		(
	 			client_ip
	 			, menu
	 			, search_word
	 			, click_link
	 			, reg_date
	 		)
 		values
	 		(
	 			#{client_ip}
	 			, #{menu}
	 			, #{search_word}
	 			, #{click_link}
	 			, now()
	 		)
